 						</div>
					<ul>
						<?php
						$args = array( 'numberposts'     => -1,'category' => 2 );
						$lastposts = get_posts( $args );
						foreach( $lastposts as $post ){ setup_postdata($post);
							if (is_sticky()) {
							?>
							<li><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></li>
							<?php } 
						}
						wp_reset_postdata();
					?>
					</ul>
					 </div>
					</div>
			</div>
		</div><!-- #content -->
	</div><!-- #primary -->

